import { HttpClientModule } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-empresas',
  templateUrl: './empresas.component.html',
  styleUrls: ['./empresas.component.css']
})
export class EmpresasComponent implements OnInit {

  public empresas: any;

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.getEmpresas();
  }

  public getEmpresas(): void {
    this.http.get('https://localhost:7092/api/empresas');

    this.empresas =
    [
      {
        EmpresaId: 1,
        NomeEmpresa: "Xing Ling ",
        NomeFantasia: "Xing Ling",
        SiglaEmpresa: 'XLG'
      },
      {
        EmpresaId: 2,
        NomeEmpresa: "Amazon.com",
        NomeFantasia: "Amazon",
        SiglaEmpresa: "AMZ"
      },
      {
        EmpresaId: 3,
        NomeEmpresa: "Google.com",
        NomeFantasia: "Google",
        SiglaEmpresa: "GooG"
      }
    ];
  }
}
